import React, { useMemo , useCallback, useRef, Fragment} from 'react';
import {Button} from "react-bootstrap";
import EmotiContainer from "./EmotiContainer/EmotiContainer";

export default function(){

    const onSelectIcon = useCallback((icon, event) => {
        console.log('click')
        event.preventDefault();
        console.log(event, icon)
    }, [])

    const button = useMemo(()=>{
        const display = false;
        return (<div className="emoticon" style={{position: 'relative', display:'inline-block',  zIndex:'5000'}}>
                    <Button  onClick={() => display = !display }>ğŸ˜€</Button>
                    <EmotiContainer display={display} onSelect={onSelectIcon}/>
                </div>)
    }, [])

    const textarea = useRef();


    return [textarea, button]
}
