import React from "react";
import BinaryButton from "./../../../common/BinnaryButton/BinnaryButton";
import "./effector-button.scss";
import { connect } from "react-redux";

const EffectorButton = props => {
    const {
        channel,
        effect,
        parameter,
        className,
        ...rest
    } = props;



    const mapStateToProps = state => {
        const effect = state.effector.channels[chnanel].effects[effect];
        return {
            value: effect[parameter]
        }
    }

    return connect(mapStateToProps)(class extends React.Component {
        render(){
            <BinaryButton value={this.props.value} className={'effector-btn ' + className} {...rest}>{props.children}</BinaryButton>
        }
    })
}

export default EffectorButton;
