import React from "react";
import {Button} from "react-bootstrap";
import "./looper.scss";

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCircle } from '@fortawesome/free-solid-svg-icons';

class Looper extends React.Component {
    constructor() {
        super();
        this.sliderElement = React.createRef();
        this.state = {
            lengths: [],
            position: 0,
            sliderAreaSize: null
        }

        for(let i = 1 / 64; i < 64 ; i = i * 2){
            this.state.lengths.push(i);
        }
    }

    sliderClickHandle(event){

    }

    componentDidMount() {
        //const { width , height } = this.sliderElement.getBoundingClientRect();

    }

    render(){
        const listItems = this.state.lengths.map( (length, index) =>{
            let display = length;
            if(length < 1){
                display = "1/" + ( 1 / length)
            }
            return (<li className="slider-list-item" key={index} data-value={length}> {display} </li>)
        })

        const listStyle = {
            transform: "translateX(" + this.state.position + "00%)"
        }

        return(
            <div className={"looper looper-deck-" + this.props.name}>
                <Button className="btn-in">IN</Button>
                <Button className="btn-out">OUT</Button>
                <MiniSlider className={"mini-slider-deck-"+ this.props.name} renderItems={listItems}/>
            </div>
        )
    }
}

export default Looper;
