import React, { useCallback, Fragment } from "react";
import { connect } from "react-redux";
import "./explorer-tree.scss";
import { isEmpty } from "./../../../../../../utils/objects/helpers";
import UUID from "uuidjs";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faFolder, faPlus, faMinus } from "@fortawesome/free-solid-svg-icons";

const ExplorerTree = props => {

    const getDirElement = useCallback((name, path, open, empty) => {
        return (
            <li
                className="list-item-dir"
                data-path={path.join("/")}>
                <button
                    className={"btn-dir " +
                        ((open) ? "btn-dir--expand" : "btn-dir--colapse")}>
                    {( !empty && <FontAwesomeIcon className="icon" icon={(open) ? faMinus : faPlus} />)}
                </button>
                
                <FontAwesomeIcon className="icon icon-dir" icon={faFolder} />
                {name}
            </li>
        )
    })
    const getFileElement = useCallback((name, path) => {
        return (
            <li
                data-path={path.join("/")}>
                {name}
            </li>
        );
    })
    console.log(props.root)

    const renderDirElements = (name, content, path) => {
        switch (typeof content) {
            case "object": {
                if (content._open === true) {
                    return (
                        <Fragment>
                            {getDirElement(name, path, true)}
                            {renderDir(content, [...path, name])}
                        </Fragment>
                    )
                } else {
                    if(isEmpty(content)){
                        return getDirElement(name, path, false, true)
                    }
                    return getDirElement(name, path, false)
                }
                break;
            }
            case "array": {
                return getFileElement(name, path)
                break;
            }
        }
    }

    const renderDir = (dir, path) => {
        return Object.entries(dir).map(([name, content]) => {
            if (name === "_open") {
                return null;
            }
            return (
                <ul className="list-dir dir-content">
                    {renderDirElements(name, content, path)}
                </ul>
            )
        })
    }

    return (
        <div className="explorer-tree">
            {renderDir(props.root, ['root'])}
        </div>
    )
}

const mapStateToProps = state => ({
    root: state.playList.root,
})

export default connect(mapStateToProps)(ExplorerTree);
