export default class Effector{
    constructor(audioContext){
        this.mainAC = audioContext;
        this.channels = {
            1: null,
            2: null,
        }
    }

    connect(inputNodes){
        const [input1, input2] = inputNodes;
        const out1 = this.mainAC.createGain(),
              out2 = this.mainAC.createGain();


        input1.connect(out1);
        input2.connect(out2);
        return [out1, out2]
    }
}
