import React, {useEffect, useState} from "react";
import "./content.scss";
import usePath from "../Hooks/usePath";

const baseMarkupsPath = './../markups'

const getMarkup = () => require.context('./../markups', true, /\.*md/).keys()

const Content = ({markdown}) => {
    const [currentPath] = usePath();
    const [md , setMd] = useState(null);

    useEffect(()=>{
        if(!currentPath.length) return;
        let pattern = "";
        currentPath.forEach( slug => pattern += "\\/?\\d?\\." + slug)
        pattern += "\\.md";
        let regex = new RegExp(pattern);
        let file = getMarkup().filter( path => regex.test(path)).replace("./", baseMarkupsPath)
         console.log("file", file)
        // import(file).then( module => console.log(module))
       // let markdown = require("./markups/2.console/0.deck.md");
         import(file).then( module => console.log(module))
       // fetch(markdown).then(respone => console.log(respone))
    },[currentPath, md])

    return (
        <main className="introduction__content">
            I will be render from md;
        </main>
    )
}

export default Content;
