import React from "react";
import { connect } from "react-redux";
import {Button} from "react-bootstrap";
import { ACTIONS, loadTrack} from "../../actions/actions";

import "./play-list.scss";

const Row = (props) => {
    const format = time => time.substr(2 ,time.length).toLowerCase();

    let {title, duration:time, source="Youtube", bmp, quality} = props.item;

    return (
        <tr className="track-list-table-row">
            <td className="track-list-table-col source">{source}</td>
            <td className="track-list-table-col title">{title}</td>
            <td className="track-list-table-col quality">{quality}</td>
            <td className="track-list-table-col time">{format(time)}</td>
            <td className="track-list-table-col bmp">{bmp}</td>
            <td className="track-list-table-col">
                <Button className="btn-dest dest-a">A</Button>
                <Button className="btn-dest dest-b">B</Button>
            </td>
        </tr>
    )
}

const mapDispachToProps = dispach =>({
    loadTrack : (track, destination) => dispach(loadTrack(track, destination))
})

const RowConnected = connect(null, mapDispachToProps)(Row);

const Table = (props) => {
    return (           
        <table className={props.classNames}>
            <thead>
                <tr>
                    {props.headers && props.headers.map( (text, index) => <th key={index}>{text}</th>)}
                </tr>
            </thead>
            <tbody>
                { props.children }
            </tbody>
        </table>
    )
}

class PlayList extends React.Component {
    constructor(){
        super();
    }

    headers = ['Source', 'Title', 'Quality', 'Time', 'Bmp', 'Destination']; 

    render(){
        return (
            (this.props.playList.length > 0 && <div className="play-list">
                <Table headers={this.headers} classNames="play-list-table">
                    {this.props.playList && this.props.playList.map( item => 
                    <RowConnected item= {item} key={item.id}/>
                    )}
                </Table>
            </div>)
        )

    }
}

const mapStateToProps = (state) => ({
    playList : state.playList.list
})

export default connect(mapStateToProps)(PlayList);
