import React, { useEffect , useRef} from "react";
import { connect } from "react-redux";
import WaveSurfer from "wavesurfer";

class Deck extends React.Component {
    constructor(){
        super();
        this.mainWave = React.createRef();
        this.smallWave = React.createRef();
        
    }


    componentDidMount(){
        if(!this.props.active) return;
        this.wavesurfer = WaveSurfer.create({
            container: this.mainWave.current,
            waveColor: 'violet',
            progressColor: 'purple',
            autoCenter : true,
            scrollParent: true,
            hideScrollbar:true,
            fillParent: false,
            minPxPerSec : 150
        });

        this.wavesurfer.on('ready', () => {
            this.wavesurfer.play();
            console.log(this.wavesurfer.backend.getPeaks(20000, 0, 20000))

        });

        this.wavesurfer.load('https://ciagnik-yt-downloader.herokuapp.com/download?url=https://www.youtube.com/watch?v=3nbY4kpCF5w');
    }

    render() {
        return (
            <div style={{height: 250 + 'px'}}>
                <div ref={ this.mainWave } > {this.props.children} </div>
                <div ref={ this.smallWave } > {this.props.children} </div>
            </div>
            )
        }
    
}

export default connect()(Deck);
