import React, { useEffect , useRef} from "react";
import { connect } from "react-redux";
import WaveSurfer from "wavesurfer";

const Deck = props => {
    let container = useRef(null);

    useEffect((someting) => {
        if(!props.active) return;
        console.log(container.current)
        let wavesurfer = WaveSurfer.create({
            container: container.current,
            waveColor: 'violet',
            progressColor: 'purple',
            autoCenter : true,
            scrollParent: true,
            fillParent: false,
            minPxPerSec : 150
        });

        wavesurfer.on('ready', function () {
            wavesurfer.play();
        });

        wavesurfer.load('https://ciagnik-yt-downloader.herokuapp.com/download?url=https://www.youtube.com/watch?v=3nbY4kpCF5w');
    }, [container])
    return (
        <div ref={ container } style={{height: 250 + 'px', overflowX: "visible"}}> {props.children} </div>
    )
}

export default connect()(Deck);
