import React from "react";
import { connect } from "react-redux";
import {setPitch, 
        togglePlay, 
        toggleCue,
    } from './../../actions/actions';
import "./deck.scss";


//import { analyze } from 'web-audio-beat-detector';

import youTubeApi from "./../../apis/yt/ytApi";
import Player from "./Player/Player";
import TrackInfo from "./TrackInfo/TrackInfo";
import PlayBackControls from "./PlayBackControls/PlayBackControls";
import PitchControl from "./PitchControl/PitchControl";


const getApi = (apiName) =>{
    switch(apiName){
        case "YouTube": return youTubeApi;

        default : return null;
    }
}

class Deck extends React.Component {

    shouldComponentUpdate(nextProps, nextState){
        return (this.props.state.paused !== nextProps.state.paused ||
        this.props.state.cueActive !== nextProps.state.cueActive)
    }

    render() {
        let api = getApi(this.props.track.source);
        let url = (api && api.getUrl(this.props.track.id)) || null;

        return (
            <div className={ "deck deck-" + this.props.name } >
                <TrackInfo name={ this.props.name } />
                <div className="flex-container">
                    <Player name={ this.props.name } url={url} />
                    <PitchControl  name={ this.props.name } onChange={ pitch => this.props.setPitch(pitch)} />
                </div>
                <PlayBackControls 
                 name={ this.props.name }
                 onPlayPress={ this.props.togglePlay.bind(null, this.props.name) }
                 onCuePress={ this.props.toggleCue.bind(null, this.props.name) }
                 playPressed={ this.props.state.paused  } 
                 cuePressed={ this.props.state.cueActive  }
                 />
            </div>
            )
        }
    
}

const mapStateToProps = (state, ownProps) => ({
    track : state.console.chanel[ownProps.name].track,
    state : state.console.chanel[ownProps.name].state,
})

const mapDispachToProps = dispach => ({
    setPitch : (value) => dispach(setPitch(value)), 
    togglePlay : (destination) => dispach(togglePlay(destination)),
    toggleCue : (destination) => dispach(toggleCue(destination))
})

export default connect(mapStateToProps, mapDispachToProps)(Deck);
