import React from "react";
import { connect } from "react-redux";
import { Button } from 'react-bootstrap';
import { toggleIsSync, toggleIsMaster } from './../../../actions';
import "./sync-bar.scss";

const SyncBar = props =>{

    return (
        <div className={"sync-bar " + "bar-deck-" + props.name }>
            <Button 
                className={"sync-btn " + ((props.syncState) ? "btn--pressed" : "")} 
                onClick={props.toggleIsSync}>
                    Sync
            </Button>
            <Button 
                className={"master-btn " + ((props.masterState) ? "btn--pressed" : "" )}
                onClick ={props.toggleIsMaster}>
                    Master
            </Button>
            
        </div>
    )
}

const mapStateToProps = (state, ownProps) => ({
    syncState : state.console.channel[ownProps.name].playBackState.isSync,
    masterState : state.console.channel[ownProps.name].playBackState.isMaster,
})

const mapDispachToProps = (dispach, ownProps) => ({
    toggleSyncState : (value) => dispach(toggleIsSync(ownProps.name, value)),
    toggleMasterState : (value) => dispach(toggleIsMaster(ownProps.name, value))
})

export default connect(mapStateToProps, mapDispachToProps)(SyncBar);
