import { PLAYBACK_STATE } from "./usePlabackState"; //getRecordUrl

class PlayerControler {
  constructor() {
    this.progressUpdaterHandler = null;
    this.current = {
      id: null,
      start: 0,
      duration: 0,
      progress: 0,
      state: null,
      buffered: 0
    };
    this.btnStateHandlers = {};
    this.progressWatchers = {};
    this.bufferedWatchers = [];
    this.progressProviders = {};
    this.currentWatchers = [];
  }

