import React, { useMemo, useCallback, useState, useRef } from "react";
import classNames from "classnames";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPen, faThLarge } from "@fortawesome/free-solid-svg-icons";
import "./edit.scss";

const Edit = ({ active, type, children, onChange = ifEmpty => null }) => {
    const [editMode, setEditMode] = useState(false);
    const controlRef = useRef();

    const buttonClasses = useMemo(() => classNames(
        'edit-btn',
        {
            "edit-btn--active": active,
            "edit-btn-before": (type.startsWith("text")),
            "edit-btn-over": (type === "image"),
        }
    ), [active])

    const catchBtnRef = useCallback((ref) => {
        if(!ref){
            return;
        } 

        ref.parentElement.classList.add("relative");
    }, [])
    console.log("chioldren", children)

    const content = useMemo(() => {
        if (!editMode) {
            return children;
        } else {
            switch (type) {
                case "image": {
                    return (
                        <>
                            <input
                                type="file"
                                style={{ display: "none" }}
                                ref={that => {
                                    controlRef.current = controlRef.current || that;
                                    controlRef.current.click();
                                }}
                                onChange={ event => {
                                    const reader = new FileReader();
                                    const file = event.target.files[0];
                                    reader.readAsArrayBuffer(file);
                                    reader.onload((e)=>{
                                        const blop = new Blop(e.target.result)
                                        onChange(blop)
                                    })
                                }} />
                            {children}
                        </>
                    )
                }
                case "text": return children;
                case "textarea": return children;
            }

        }
    }, [children, editMode])

    return (
        <>
            {active &&
                <button
                    className={buttonClasses}
                    ref={catchBtnRef}
                    onClick={setEditMode.bind(null, edit => !edit)}>
                    <FontAwesomeIcon icon={faPen} />
                </button>
            }
            {content}
        </>
    )
}

export default Edit;
