import React, {useState, useCallback} from "react";
import IconBar from "./../../IconBar/IconBar"
import "./comment.scss";
import {Form, Button} from "react-bootstrap";

const CommentContent = ({
        header,
        text, 
        editable,
        onChange,
        onDelete = ()=> null, 
        creationMode}) => {
    const [content, setConntent] = useState(text || "")
    const [editMode, setEditMode] = useState(false);

    const changeMode = useCallback((mode)=>{
        
        setEditMode(mode)
    }, [])

    const updateComment = useCallback((e)=>{
        setConntent(e.target.value);
        if(e.target.value === ""){
            changeMode(false)
        }
    },[setConntent, changeMode])
    
    const onBlur = useCallback(()=>{
        (content === "") ? changeMode(false) : changeMode(true);
    },[content, changeMode])


    const onSubmit = useCallback((e)=>{
        e.preventDefault();
        if(!onChange) return;
        if(creationMode){
            setConntent("")
        }
        changeMode(false)
        onChange(content)
    }, [content, onChange, setConntent, changeMode])

    return (
        <Form onSubmit={onSubmit}
            onFocus={changeMode.bind(null, true)}
            onBlur={onBlur}>
            <Form.Group>
                <Form.Label>
                    { header }
                </Form.Label>
                { editable && !creationMode &&
                        <IconBar items={{
                            edit: changeMode.bind(null, true),
                            delete: onDelete.bind(null)
                        }}
                        />}
                <Form.Control 
                    className="record-comments-control"
                    type="textarea" 
                    as="textarea"
                    name="comment-conntent"
                    disabled={!editMode && !creationMode}
                    value={content}
                    onChange={updateComment}/>
            </Form.Group>
            {editMode && <Button type="submit">Post</Button>}
        </Form>
    )
}

export default CommentContent;
