//Actions types 
const ACTIONS = {
    ADD_LEFT_TRUCK : "Add left truck",
    ADD_RIGHT_TRUCK : "Add rigth truck",
}

// actions creators

export function addLeftTruck(data){
    return {
        type: ACTIONS.ADD_LEFT_TRUCK,
        data
    }
}

export function addRightTruck(data){
    return {
        type: ACTIONS.ADD_RIGHT_TRUCK,
        data
    }
}
//---------------------- Serching

ACTIONS = {
    ...ACTIONS,
    SEARCH_INPUT : "Tap in to Search component",
    SET_SEARCH_RESULTS : "Set YT(or oteher source) serch results",
}

export function searchInput(text){
    return {
        type: ACTIONS.SEARCH_INPUT,
        text: text
    }
}

export function setSearchResults(results){
    return {
        type: ACTIONS.SET_SEARCH_RESULTS,
        results : results,
    }
}

// --- PlayList

ACTIONS = {
    ...ACTIONS,
    PUSH_TRACK : "Push tack on end of list",
}

export function pushTrackToList(track){
    return {
        type: ACTIONS.PUSH_TRACK,
        track : track
    }
};


export { ACTIONS }
