import React, { Fragment } from "react";
import { connect } from "react-redux";
import LoadingBar from 'react-redux-loading-bar';
import NavBar from "./NavBar/NabBar"
import { Col, Row, Container } from "react-bootstrap";
import classNames from "classnames";
import "./header.scss";

class Header extends React.Component {
  constructor() {
    super();
    this.state = {
      translateY : 0,
    }
    this.barState = {
      barElement : React.createRef(null),
      barElementHeight : 0,
    }
    this.scrollState = {
      prevScrollDirection: null, // "up" or "down",
      prevScrollPosition: 0,
      snapPosition: null,
    }
  }

  startWatching() {
    window.addEventListener('scroll', this.scrollWatching.bind(this))
  }

  stopWatching() {
    window.removeEventListener('scroll', this.scrollWatching.bind(this))
  }

  scrollWatching(event) {
    //console.log(window.scrollY)
    const scrollDirection = (window.scrollY - this.prevScrollPosition < 0) ? "up" : "down";
    if (scrollDirection !== this.prevScrollDirection) {
      this.snapPosition = window.scrollY;

      this.barElement.current.style.transform = `translateY(-${this.barElementHeight}px)`
    }

    if (scrollDirection === "up") {

    }

    //    console.log(window.scrollY)
    if (window.scrollY === 0) {
      this.barElement.current.classList.remove("bar--sticki");
      this.barElement.current.style.transform = "none"
    } else {


      if (scrollDirection === "up") {
        this.barElement.current.classList.add("bar--sticki");
        let y = - this.barElementHeight - (window.scrollY - this.snapPosition);
        y = Math.min(y, 0);
        this.barElement.current.style.transform = `translateY(${y}px)`
      } else {
        let y = (window.scrollY - this.snapPosition);
        console.log(y)
        this.barElement.current.style.transform = `translateY(-${y}px)`
        if (y > this.barElementHeight) {
          this.barElement.current.classList.remove("bar--sticki")
        }
      }
    }






    this.prevScrollPosition = window.scrollY;
    this.prevScrollDirection = scrollDirection;
  }

  updateState(prevProps) {
    if (this.props.sticki) {
      this.startWatching();
    } else {
      this.stopWatching();
    }
  }

  componentDidMount() {
    this.updateState();
    this.barElementHeight = this.barElement.current.getBoundingClientRect().height;
  }

  componentDidUpdate(prevProps) {
    this.updateState(prevProps);
  }

  render() {
    return (
      <Fragment>
        <LoadingBar className="loading-bar" />
        <header
          className={classNames("top-bar", {
            "bar--hidden": this.props.hidden,
            "bar--dissabled": this.props.dissabled
          })}
          ref={this.barElement}>
          <Container className="app layout container-xl" >
            <Row>
              <Col>
                <NavBar className="navigation-bar" />
              </Col>
            </Row>
          </Container>
        </header>
      </Fragment>
    )
  }
}

const mapStateToProps = state => ({
  sticki: state.header.sticki,
  hidden: state.header.hidden,
  disabled: state.header.disabled,
})

export default connect(mapStateToProps)(Header);
