import ACTIONS from "./../../actions";


const initState = {
    current: null,
}

export default function (state = initState, action){
    switch(action.type){

        case ACTIONS.P_SET_PROFILE : {
            return {
                ...state,
                current : action.profile
            }
        }

        default: return state;
    }
}
