import React, {useState, useEffect, useCallback, useRef}  from "react";
import {reqUpdateRec} from "./../../../../actions";
import {connect} from "react-redux";
import {Form , Button} from "react-bootstrap"
import "./record-details.scss";

const RecordDetails = ({record, userId, requestUpdate}) => {
    const [editMode, setEditMode] = useState(false);
    const [editable, setEditable] = useState(false);

    const [userNickname, setUserNickname] = useState("")
    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");
    const [generes, setGeneres] = useState([]);

    const descriptionTextArea = useRef();


    const setFromRecord = useCallback((record) => {
        if(record.user.id === userId){
            setEditable(true);
        }
        setUserNickname(record.user.nickname || "");
        setTitle(record.title || "")
        setDescription(record.description || "")
        setGeneres(record.generes || []);
    }, [userId, 
        setEditable,
        setTitle, 
        setDescription, 
        setGeneres, 
        setUserNickname])

    useEffect(()=>{
        if(!record) return;
        setFromRecord(record)
    },[record, setFromRecord])


    const toggleMode = useCallback(()=>{
        const mode = !editMode;
        setEditMode(mode)
        if(!mode){
            setFromRecord(record)
        }

    }, [record, editMode, setEditMode])

    const updateTitle = useCallback((event) => {
        setTitle(event.target.value);
    }, [setTitle])

    const updateDescription = useCallback( event => {

        setDescription(event.target.value)
    },[setDescription])

            
    const updateGeneres = useCallback( event => {
        let generes = event.target.value.split(",").map( genere => genere.trim())
        setGeneres(generes)
    }, [setGeneres])
        
    const updateRecordDetails = useCallback( event => {
        event.preventDefault();
        const title = event.target.elements.title.value;
        const description = event.target.elements.description.value;
        const generes = event.target.elements.generes.value || "";
        const generesArr = generes.split(",").map( str => str.trim());
        console.log("call update")
        requestUpdate(record.id , {
            title,
            description,
            generes: generesArr
        })
        setEditMode(false)
    }, [record, setEditMode, requestUpdate])

    //ajust text areaf size 
    useEffect(() => {
        const el = descriptionTextArea.current;
        el.style.height = 0;
        el.style.height = el.scrollHeight + 10 + "px";
    }, [description, descriptionTextArea])

    return (
        <Form className="record-details" onSubmit={updateRecordDetails}>
            <Form.Group className="record-title">
                <Form.Label>User: </Form.Label>
                <Form.Control name="user" type="text" value={userNickname} disabled/>
            </Form.Group>
            {editable && <Form.Check 
                type="switch" 
                id="switch-edit-mode" 
                label="edit" 
                className="switch-edit-mode" 
                value={editMode} 
                onChange={toggleMode}/>}
            <Form.Group className="record-title">
                <Form.Label>Title: </Form.Label>
                <Form.Control name="title" type="text" value={title} onChange={updateTitle} disabled={!editMode}/>
            </Form.Group>
            <Form.Group className="record-description">
                <Form.Label>Description: </Form.Label>
                <Form.Control name="description" as="textarea" ref={descriptionTextArea} value={description} onChange={updateDescription} disabled={!editMode}/>
            </Form.Group>
            <Form.Group className="genere-list">
                <Form.Label>Generes: </Form.Label>
                <Form.Control 
                    type="text"
                    name="generes"
                    value={generes.join(", ")} 
                    onChange={ updateGeneres } 
                    disabled={!editMode}/>
            </Form.Group>
            {editMode && <Button type="submit"> Save </Button>}
        </Form>
    )
}

const mapStateToProps = state => ({
    userId : state.user.dbId
})

const mapDispatchToProps = dispatch => ({
    requestUpdate : (recordId, dataChanges) => dispatch(reqUpdateRec(recordId, dataChanges))
})

export default connect(mapStateToProps, mapDispatchToProps)(RecordDetails)
