import React from "react";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import {faStar as fillStar} from "@fortawesome/free-solid-svg-icons";
import {faStar as emptyStar} from "@fortawesome/free-regular-svg-icons";

const Testimonial = ({
    data : {
        stars,
        text,
        author: {
            name: authorName,
            position: authorPosition,
            image: authorImage,
        } = {}
    } = {}
}) => {
    console.log(stars)
    return (
        <div className="testmionial">
            <div className="stars">
                {Array(5).fill(1).map((e, i) => 
                    <FontAwesomeIcon icon={((i < stars) ? fillStar : emptyStar )}/>)
                }
            </div>
            <p className="text">
                {text}
            </p>
            <div className="testimonial-autor">
                <img src={authorImage || "/user-anonymous.png"} alt="testimonial author"/>
                <h5>{authorName}</h5>
                <h6>{authorPosition}</h6>
            </div>
        </div>
    )
}

export default Testimonial;
