import React, {useEffect, useMemo} from "react";
import {setFooterType} from "./../../actions";
import {connect} from "react-redux";
import {Col, Container, Row} from "react-bootstrap";
import Content from "./Content/Content"
import {useRouteMatch} from "react-router-dom";
import Menu from "./Menu/Menu";

const Introduction = ({dispatch}) => {
    let { path: _path, url: _url} = useRouteMatch();

    useEffect(() => {dispatch(setFooterType("default"))}, [])

    const path = useMemo(()=>{
        if(!_path || !_url) return [];
        const base = _path.split("/")[0];
        console.log("base is", base)
        return _url.split("/").filter( slug => slug !== base);
    }, [_path, _url])

    console.log(path);

    return (
            <div className="introduction">
                <Container>
                    <Row>
                        <Col lg={10}>
                            <Content/>
                        </Col>
                        <Col lg={2}>
                            <Menu/>
                        </Col>
                    </Row>
                </Container>
            </div>
    )
}

export  default connect()(Introduction);
