import {  ACTIONS } from "./../actions/actions.js";


const deck = {
    track : {
        title : "",
        bmp : 0,
        quality : "",
        duration : "",
        source : "",
        id: "",
        thumbnail : {}
    }, // 
    playBackState : {
        paused: true,
        cuePoint: 0,
        cueActive: false,
        pitch: 0,
        timeLeft : null
    }
}

const chanel = {
    volume: 100,
    low: 100,
    mid : 100,
    high : 100,
    gain: 100
}

const initState = {
    chanel: {
        A:{
            ...deck
        },
        B:{
            ...deck
        }
    },
   
    mikser : {
        chanelA : {
            ...chanel,
        },
        chanelB : {
            ...chanel,
        },
        fader : {
            position : 50 //A: 0 <-> B:100
        }
    }
}

function consoleReducer(state = initState, action){
    switch(action.type){
        case ACTIONS.LOAD_TRACK : {
            let destination = action.destination;
            let track = action.track;
            let newState = {...state};
            if(newState.chanel[destination]){
                newState.chanel[destination].track = {
                    title : track.title,
                    bmp : track.bpm || 0,
                    quality : track.quality,
                    duration : track.duration,
                    source : track.source,
                    id : track.id,
                    thumbnail : track.thumbnail,
                }
            }
            return newState;
        }

        case ACTIONS.SET_PITCH : {
            const _state = {...state};
            if(_state.chanel[action.destination]){
                _state.chanel[action.destination].playBackState.pitch = action.pitch;
            }
            return _state;
        }

        case ACTIONS.TOGGLE_PLAY : {
            const _state = {...state}
            console.log(action.destination)
            if(_state.chanel[action.destination]){
                _state.chanel[action.destination]
                .playBackState.paused = !state.chanel[action.destination]
                                .playBackState.paused; 
            }
            return state;
        }

        case ACTIONS.TOGGLE_CUE : {
            const _state = {...state}
            if(_state.chanel[action.destination]){
                _state.chanel[action.destination]
                .playBackState.cueActive = !state.chanel[action.destination]
                                .playBackState.cueActive; 
            }
            return _state;
        }

        default : return state;
    }
}

export default consoleReducer;
