import React, { useState , useEffect, useRef} from "react";
import "./pitch-control.scss";

class PitchControl extends React.Component{
    constructor() {
        super();

        this.sliderRangeElement = React.createRef(null);
        this.sliderThumbElement = React.createRef(null);
        this.sliderAreaElement = React.createRef(null);
    }

    state = {
        sliderRange: null,
        sliderCurrnetPosition: null,
        sliderPrevPosition: null,

        startDraging: false,
    }


    mouseDownHandle = (event, sliderRange) =>{
      //  console.log(this);
        if(event.target === this.sliderAreaElement.current || event.target === this.sliderRangeElement.current){ // area clicked
            let clickY = event.clientY - this.sliderAreaElement.current.getBoundingClientRect().top;
            this.setState ( state => {
                const _state = {...state};
                _state.sliderPrevPosition = state.sliderCurrnetPosition;
                _state.sliderCurrnetPosition = clickY;
                return _state;
            })
          //  console.log(this.state.sliderCurrnetPosition);
            document.addEventListener('mouseup', this.mouseUpHandler);
            document.addEventListener('dragend', this.mouseUpHandler);
        } else if(event.target === this.sliderThumbElement.current){ // thumb clicked

            const shiftY = event.clientY - this.sliderThumbElement.current.getBoundingClientRect().top;
            const topOffset = this.sliderAreaElement.current.getBoundingClientRect().top;
       
            let sliderRange = this.state.sliderRange;
        
            document.addEventListener('mousemove', event =>  this.mouseMoveHandler.call(this, event, shiftY, topOffset, sliderRange));
        }
    }

    mouseUpHandler = event => {
        this.setState( state => {
            const _state = {...state};
            _state.sliderCurrnetPosition = state.sliderPrevPosition;
            return _state; 
        })
        document.removeEventListener('mouseup', this.mouseUpHandler);
        document.removeEventListener('dragend', this.mouseUpHandler);
    }

    mouseMoveHandler = (event, shiftY, topOffset, sliderRange) =>{
    //    console.log('moving', event, shiftY)
       // console.log(sliderAreaElement.current)
        let position = event.clientY - topOffset;
        //console.log(sliderRange);
        this.setState( state => {
            const _state = {...state};
            _state.sliderCurrnetPosition = position;
            return _state;
        })
        //position = Math.max(position, 0);
        //position = Math.min(position, sliderRange);
        //console.log('mouse move', this.state.sliderRange);
        //console.log("position: " , position);

    }

    componentDidMount(){
        this.sliderAreaElement.current.addEventListener('mousedown',  this.mouseDownHandle.bind(this));
    }

    componentDidUpdate(){
        //console.log(parseInt(window.getComputedStyle(this.sliderRangeElement.current).height))
        let top = parseInt(window.getComputedStyle(this.sliderThumbElement.current).top)
        console.log("top before state update", top)
        this.setState( state => {
            const _state = {...state};
            _state.sliderCurrnetPosition = top;
            _state.sliderRange = parseInt(window.getComputedStyle(this.sliderRangeElement.current).height);
        })

        console.log('after update' , top , this.state);
    }

    

    render(){
        console.log('render');
            return (
                <div className={"pitch-control " + "pitch-" + this.props.name}>
                    <div ref={this.sliderAreaElement} className="pitch-area">
                        <div ref={this.sliderRangeElement} className="slider-range"/>
                        <div ref={this.sliderThumbElement} style={this.state.sliderCurrnetPosition && {top: this.state.sliderCurrnetPosition}}  className="slider-thumb"/>
                    </div>
                </div>
            )
        }
}

export default PitchControl;
