import { ACTIONS } from "../actions/actions";

const initState = {
    searchString: "",
    searchResult: {}
}

function serachReducer(state = initState, action){
    console.log(action)
    switch(action.type){
        case ACTIONS.SEARCH_INPUT : 
        return {
            ...state, 
            searchString : action.text
        }

        default : return state;
    }
}

export default serachReducer;
