import React from "react";
import { connect } from "react-redux";
import { ACTIONS } from "../../actions/actions";

const Row = (props) => {
    let {title, duration:time, source="Youtube", bmp, quality} = props.item;
    return (
        <tr className="track-list-table-row">
            <td className="track-list-table-col source">{source}</td>
            <td className="track-list-table-col title">{title}</td>
            <td className="track-list-table-col quality">{quality}</td>
            <td className="track-list-table-col time">{time}</td>
            <td className="track-list-table-col bmp">{bmp}</td>
            <td className="track-list-table-col btn-a">A - btn</td>
            <td className="track-list-table-col btn-b">B - btn</td>
        </tr>
    )
}

const Table = (props) => {
    return (           
        <table >
            <thead>
                <tr>
                    {props.headers && props.headers.map( (text, index) => <th>{text}</th>)}
                </tr>
            </thead>
            <tbody>
                { props.children }
            </tbody>
        </table>
    )
}

class PlayList extends React.Component {
    constructor(){
        super();
    }

    headers = ['Source', 'Title', 'Quality', 'Time', 'Bmp', 'Destination']; 

    render(){
        return (
            <div className="play-list">
                <Table headers={this.headers} classNames="play-list-table">
                    {this.props.playList && this.props.playList.map( item => 
                    <Row item= {item} key={item.id}/>
                    )}
                </Table>
            </div>
        )

    }
}

const mapStateToProps = (state) => ({
    playList : state.playListReducer?.list
})

export default connect(mapStateToProps)(PlayList);
