import React, {useEffect, useState} from "react";
import "./content.scss";
import usePath from "../Hooks/usePath";

const baseMarkupsPath = './../markups/'

const getMarkup = () => require.context('./../markups', true, /\.*md/).keys()

const load = (module) => import(`./../markups/${module}`).then( module => module.default);


const Content = ({markdown}) => {
    const [currentPath] = usePath();
    const [md , setMd] = useState(null);

    useEffect(()=>{
        if(!currentPath.length) return;
        let pattern = "";
        currentPath.forEach( slug => pattern += "\\/?\\d?\\." + slug)
        pattern += "\\.md";
        let regex = new RegExp(pattern);
        let file = getMarkup().filter( path => regex.test(path))[0]?.replace("./", "");
        if(!file) return;
        load(file)
            .then(md => fetch(md))
            .then(response => response.text())
            .then(md => setMd(md))
    },[currentPath, setMd])

    return (
        <main className="introduction__content">
            {md}
        </main>
    )
}

export default Content;
