import React, {useCallback, useEffect, useRef, useState} from "react";
import UUID from "uuidjs";
import style from "./slider.scss"

const rmLast = (arr, direction) => {
    let last = arr.length - 1;
    let _arr = [...arr];
    while (last >= 0) {
        if (direction === "next" && arr[last] < 0) break;
        if (direction === "prev" && arr[last] > 0) break;
        _arr.pop();
        last--;
    }
    return _arr;
}

const sumLastSameDirection = arr => {
    let _arr = [...arr];
    if (arr[arr.length - 1] * arr[arr.length - 2] > 0) {
        let last = _arr.pop();
        let prev = _arr.pop();
        _arr.push(last + prev);
    }
    return _arr;
}

const Slider = ({slides: initSlides, next: nextHandle, prev: prevHandle, minSlideWidth = style.minSlideWidth}) => {
    const slider = useRef();
    const [_containerRect, setContainerRect] = useState(null);
    const [slideWidth, setSlideWidth] = useState(null);
    const [visibleSlides, setVisibleSlides] = useState(null);
    const [slides, setSlides] = useState([...initSlides, ...initSlides, ...initSlides]);
    const [isTransitionRunning, setTransitionRunning] = useState(false);
    const transitionActive = useRef();
    const [actionQueue, setActionQueue] = useState([]);
    const shift = useRef();
    const balancedShift = useRef();
    const translate = useRef();


    const containerRect = useCallback(() => {
        if (!slider.current) return 0;
        let rect = _containerRect
        if (!rect) {
            rect = slider.current.parentElement.getBoundingClientRect()
            setContainerRect(rect);
        }
        return rect;
    }, [_containerRect, setContainerRect])

    const setTransition = useCallback(value => {
        if (!slider.current) return;
        if(value){
            slider.current.classList.add("slider-transition--active");
            transitionActive.current = true;
        } else {
            slider.current.classList.remove("slider-transition--active");
            transitionActive.current = false;
        }
    }, [slider, setTransitionRunning])


    useEffect(() => {
        const transitionEnd = () => setTransitionRunning(false);
        window.addEventListener("transitionend", transitionEnd);
        window.addEventListener("transitioncancel", transitionEnd)
        window.addEventListener("mozTransitionEnd", transitionEnd);
        window.addEventListener("webkitTransitionEnd", transitionEnd);
        window.addEventListener("oTransitionEnd", transitionEnd);
        return () => {
            window.removeEventListener("transitionend", transitionEnd);
            window.removeEventListener("transitioncancel", transitionEnd)
            window.removeEventListener("mozTransitionEnd", transitionEnd);
            window.removeEventListener("webkitTransitionEnd", transitionEnd);
            window.removeEventListener("oTransitionEnd", transitionEnd);

        }
    }, [setTransitionRunning])

    const setTranslate = useCallback(target => {
        if (!slider.current) return;
        slider.current.style.transform = `translateX(${target}px)`
        translate.current = target;
    }, [slider, translate])

    const setPosition = useCallback(position => {
        if (!slider.current) return;
        setTranslate(position * slideWidth)
        setTransitionRunning(true);
    }, [slider, slideWidth, setTransitionRunning, setTranslate])

    useEffect(()=>{
        if (!containerRect || !minSlideWidth || !slider.current) return;
        const containerWidth = containerRect().width;
        const visibleSlides = ~~(containerWidth / parseInt(minSlideWidth));
        setVisibleSlides(visibleSlides);
        console.log("visible slides" , visibleSlides)
    }, [containerRect, minSlideWidth, slider, setVisibleSlides])

    useEffect(() => {
        shift.current = -initSlides.length;
        balancedShift.current = shift.current;
        setPosition(shift.current);
    }, [slideWidth, setPosition, shift, balancedShift, initSlides])

    useEffect(() => {
        const resetSize = () => setContainerRect(null);
        window.addEventListener("resize", resetSize);
        return () => window.removeEventListener("resize", resetSize);
    }, [setContainerRect])

    useEffect(() => {
        if(!visibleSlides || !containerRect) return;
        const slideWidth = containerRect().width / visibleSlides
        setSlideWidth(slideWidth);
        // setVisibleSlides(visibleSlides);
        console.log("slide with ", slideWidth)
        slider.current.style.width = slideWidth * slider.current.children.length + "px";
    }, [containerRect,  visibleSlides]);

    const appendSlides = useCallback(amount => {
        console.log("appnedn", amount)
        setSlides(slides => {
            const _slides = [...slides];
            for (let i = 0; i < amount; i++) {
                const slide = _slides.shift();
                _slides.push(slide)
            }
            return _slides;
        })
    }, [setSlides])

    const prependSlides = useCallback(amount => {
        console.log("prepend", amount)
        setSlides(slides => {
            const _slides = [...slides];
            for (let i = 0; i < amount; i++) {
                const slide = _slides.pop();
                _slides.unshift(slide)
            }
            return _slides;
        })
    }, [setSlides])

    const balance = useCallback(() => {
        const diff = balancedShift.current - shift.current;
        console.log("balancde shift", balancedShift.current, shift.current);
        console.log("diff", diff)
        if(!diff) {
            setTransition(true);
            return;
        }
        (diff < 0) ? prependSlides(Math.abs(diff)) : appendSlides(diff);
        setTransition(false);
        shift.current = balancedShift.current;
        setPosition(balancedShift.current);
        console.log("the diff", diff)

    }, [shift, balancedShift, prependSlides, appendSlides, setTransition, setTransitionRunning])

    const action = useCallback(() => {
        console.log("in action", actionQueue)
        if (!actionQueue.length) {
            balance();
            return;
        }
        console.log("conduct action")
        const queue = actionQueue;
        const direction = queue.shift();
        setTransition(false);
        (direction > 0) ? appendSlides(direction) : prependSlides(Math.abs(direction));
        setPosition(shift.current + direction);
        setTimeout(() => {
            setTransition(true);
            setPosition(shift.current);
            setActionQueue(queue)
        }, 0)
    }, [setTransition, setPosition, actionQueue, setActionQueue, balance])

    useEffect(() => {
        console.log("call aciton", isTransitionRunning)
        if (!isTransitionRunning) {
            action();
        }
    }, [isTransitionRunning, actionQueue])

    const next = useCallback(() => {
        setActionQueue(queue => {
            const _queue = rmLast(queue, "prev");
            return sumLastSameDirection([..._queue, 1])
        })
    }, [setActionQueue])

    useEffect(() => nextHandle && nextHandle(next), [next, nextHandle])

    const prev = useCallback(() => {
        setActionQueue(queue => {
            const _queue = rmLast(queue, "next");
            return sumLastSameDirection([..._queue, -1])
        })
    }, [setActionQueue])

    useEffect(() => prevHandle && prevHandle(prev), [prev, prevHandle])

    const endDraging = useCallback(() => {
        shift.current = Math.round(translate.current / slideWidth);
        setPosition(shift.current);
    }, [translate, setTranslate, shift, slideWidth])

    const mouseMove = useCallback((shiftX, event) => {
        const clientX = (event.type === "touchmove") ? event.touches[0].clientX : event.clientX;
        setTranslate(clientX - shiftX);
    }, [setTranslate])


    const mouseDown = useCallback(event => {
        const sliderContainer = event.target.closest(".carousel-slider-container");
        if(!sliderContainer) return;
        const rect = sliderContainer.getBoundingClientRect();
        const clientX = (event.type === "touchstart") ? event.touches[0].clientX : event.clientX;
        const shiftX = clientX - rect.left - shift.current * slideWidth;
        const mouseMoveWithArgs = mouseMove.bind(null, shiftX);

        const mouseUp = () => {
            window.removeEventListener("mousemove", mouseMoveWithArgs);
            window.removeEventListener("mouseup", mouseUp);
            window.removeEventListener("touchmove", mouseMoveWithArgs);
            window.removeEventListener("touchend", mouseUp)
            setTransition(true);
            endDraging();
        }

        window.addEventListener("mousemove", mouseMoveWithArgs);
        window.addEventListener("touchmove", mouseMoveWithArgs);
        window.addEventListener("mouseup", mouseUp);
        window.addEventListener("touchend", mouseUp);
        setTransition(false);
    }, [setTransition, shift, slideWidth, endDraging])

    console.log("sift" , shift.current)
    console.log("aciton queue", actionQueue)
    return (
        <div className="carousel-slider-container">
            <ul className="carousel-slider" ref={slider} onMouseDown={mouseDown} onTouchStart={mouseDown} onDrag={ e => e.preventDefault()}>
                {slides && slides instanceof Array && slides.map(slide => (
                    <li key={UUID.genV1()} className={"slide"} style={{width: minSlideWidth || "initial"}}>{slide}</li>
                ))}
            </ul>
        </div>
    )
}
export default Slider;
