import {useState, useCallback} from "react"

export function useDoubleClickExplicy({clickHandler, doubleClickHandler}){
    const [lastClick, setLastClick] = useState(null);
    const [timerHandler, setTimerHandler] = useState(null);
    const liClickHandler = useCallback((...args)=>{
        const now = new Date().getTime();
        if(timerHandler === null){
            setLastClick(now);
            setTimerHandler(setTimeout(()=>{
                clickHandler(...args);
                clearTimeout(timerHandler);
                setTimerHandler(null);
            },200))
        } else if ( now - lastClick <200){
            console.log(doubleClickHandler)
            doubleClickHandler(...args);
            setLastClick(null)
            clearTimeout(timerHandler);
            setTimerHandler(null);
        }
        console.log( now - lastClick)
    }, [lastClick, setLastClick, timerHandler, setTimerHandler])

    return [liClickHandler]
}
