import { ACTIONS } from "./../../../actions";
import STATES from "./stateDef";

const initState = {
    recName: "",
    recId: null,
    recordingState: STATES.IDLE,
    startTime: null,
    tracklist: [],
}

export default function headerState(state = initState, action) {
    switch (action.type) {
        case ACTIONS.R_START_RECORDING: {
            return {
                ...state,
                recordingState: STATES.INIT,
                recName: action.recName,
                recId: action.id,
                startTime: new Date().getTime(),
            }
        }

        case ACTIONS.R_END_RECORDING: {
            return {
                ...state,
                recordingState: STATES.IDLE,
                recName: "",
                tracklist: []
            }
        }

        case ACTIONS.R_RECORDING: {
            return {
                ...state,
                recordingState: STATES.RECORDING,
            }
        }

        case ACTIONS.R_RECORDING_FAIL: {
            return {
                ...state,
                recordingState: STATES.FAIL,
                recName: "",
                tracklist: []
            }
        }

        //------------
        case ACTIONS.LOAD_TRACK: {
            if(state.recordingState !== STATES.RECORDING){
                return state;
            }

            const nextTracklist = state.tracklist;
            nextTracklist.push({
                id: action.track.id,
                destination: action.destination,
                time: null
            })
            return {
                ...state,
                tracklist: nextTracklist
            }
        }

        case ACTIONS.CANCEL_CUE_AND_PLAY:
        case ACTIONS.TOGGLE_PLAY:{
            if(state.recordingState !== STATES.RECORDING){
                return state;
            }
            let found = false;
            const nextTracklist = state.tracklist.slice().reverse.map( track => {
                if(track.destination === action.destination && !track.time && !found){
                    track.time = new Date().getTime() - state.startTime;
                    found = true;
                }
                return track;
            });
            return {
                ...state,
                tracklist: nextTracklist
            }
        }


        default: return state;
    }
}
