import { put, select, takeEvery } from 'redux-saga/effects';
import { ACTIONS, setProfileNickname } from "../../actions";
import { getApi } from "./../../apis/apiProvider";


export default function* watcher() {
    yield takeEvery(ACTIONS.U_REQ_UPDATE_NICKNAME, handel);
}

const getToken = state => state.user.token
const userId = state => state.user.id;

function* handel(action) {
    const token = yield select(getToken);

    try {
        const { callQuery, queries } = getApi("UserAssets");

        const query = queries.updateNicknameQl;

        const response = yield callQuery(query, token, {nickname: action.nickname});
        console.log(response);
        const status = response?.data?.udpateMyNick;
        if(status.errors){
            throw new Error("Can't update nickname." + status.message)
        }
        if(response.errors || !status.success){
            throw new Error("Can't update nickname in database")
        }

        const id = select(userId)
        yield put(setProfileNickname(id, action.nickname))

    } catch (e) {
        console.log(e.message)
    }
}
