import React, {useCallback, useEffect, useRef, useState} from "react";
import UUID from "uuidjs";
import style from "./slider.scss"

const Slider = ({slides: initSlides, next:nextHandle, prev: prevHandle, minSlideWidth = style.minSlideWidth}) => {
    const slider = useRef();
    const [_containerWidth, setContainerWidth] = useState(null);
    const [slideWidth, setSlideWidth] = useState(null);
    const [slides, setSlides] = useState([...initSlides, ...initSlides, ...initSlides]);
    const shift = useRef();

    const containerWidth = useCallback(() => {
        if (!slider.current) return 0;
        let width = _containerWidth
        if (!width) {
            width = slider.current.parentElement.getBoundingClientRect()?.width
            setContainerWidth(width);
        }
        return width;
    }, [_containerWidth, setContainerWidth])

    const setTransition = useCallback( value =>{
         if(!slider.current) return;
         slider.current.style.transition = (value) ? "transform 1s ease 0s" : "unset";
    }, [slider])

    const setPosition = useCallback( position =>{
        console.log(position)
        if(!slider.current) return;
        slider.current.style.transform = `translateX(${position * slideWidth }px)`
    }, [ slider, slideWidth])

    useEffect(()=>{
        shift.current = -initSlides.length;;
        setPosition(shift.current);
    }, [slideWidth, setPosition, shift])

    useEffect(() => {
        const resetSize = () => setContainerWidth(null);
        window.addEventListener("resize", resetSize);
        return () => window.removeEventListener("resize", resetSize);
    }, [setContainerWidth])

    useEffect(() => {
        if (!containerWidth() || !minSlideWidth || !slider.current) return;
        const visibleSlides = ~~(containerWidth() / parseInt(minSlideWidth));
        const slideWidth = containerWidth() / visibleSlides
        setSlideWidth(slideWidth);
        slider.current.style.width = slideWidth * slider.current.children.length + "px";
    }, [containerWidth, minSlideWidth,  setSlideWidth]);


    const next = useCallback(()=>{
        console.log("call next")
        // if(!slider.current) return;
        // const first = slider.current.firstChild;
        // slider.current.removeChild(first);
        // slider.current.appendChild(first);
        //
        // // setTransition(false);
        //  setPosition(shift.current++)
        // setTransition(true);
        //  setPosition(shift.current--)

    }, [ setTransition, setPosition])

    useEffect(()=> nextHandle && nextHandle(next), [next, nextHandle])

    const prev = useCallback(()=>{
        console.log("prev slide")
    }, [])

    useEffect(()=> prevHandle && prevHandle(prev), [prev, prevHandle])

   // console.log(containerWidth(), slideWidth, parseInt(minSlideWidth))
    return (
        <ul className="carousel-slider" ref={slider}>
            {slides && slides instanceof Array && slides.map(slide => (
                <li key={UUID.genV1()} className={"slide"} style={{width: minSlideWidth || "initial"}}>{slide}</li>
            ))}
        </ul>
    )
}
export default Slider;
