import React, { useEffect , useRef} from "react";
import { connect } from "react-redux";
import WaveSurfer from "wavesurfer";

import WaveSurferC from "./WaveSurfer/WaveSurfer";

import { analyze } from 'web-audio-beat-detector';
import WaveSurferContext from "./WaveSurfer/WaveSurfer-context";

class Deck extends React.Component {
    constructor(){
        super();
        this.mainWave = React.createRef();
        this.smallWave = React.createRef();
        
        this.state.join = {
            peaks: [],
            position: 0,
        }
    }


    componentDidMount(){
        if(!this.props.active) return;
        this.bigWaveSurfer = WaveSurfer.create({
            container: this.mainWave.current,
            waveColor: 'violet',
            progressColor: 'purple',
            autoCenter : true,
            scrollParent: true,
            hideScrollbar:true,
            fillParent: false,
            height: 130,
            minPxPerSec : 150
        });

        this.smallWaveSurfer = WaveSurfer.create({
            container: this.smallWave.current,
            fillParent: true,
            scrollParent : false,
            hideScrollbar: true,
            scrollParent : false,
            height: 50,
        })

        let duration = 0;
        this.bigWaveSurfer.on('ready', () => {
           // this.bigWaveSurfer.play();
            duration = this.bigWaveSurfer.getDuration();
            let resolution = 430;
            let peaks = this.bigWaveSurfer.backend.getPeaks(resolution, 0, resolution);
            this.smallWaveSurfer.load(null, peaks);
            startSync();
            //this.calcBmp();
        });

        this.smallWaveSurfer.on('seek', (progress) => {
            this.bigWaveSurfer.seekTo(progress);
            if(!this.bigWaveSurfer.isPlaying()){
                this.bigWaveSurfer.play();
            }
        })

        let syncHandle = null;
        let startSync = () => {
            syncHandle = setInterval(() => {
                let progress = this.bigWaveSurfer.getCurrentTime() / duration;
                this.smallWaveSurfer.drawer.progress(progress);
            }, 100);
        }

        let url = "https://ciagnik-yt-downloader.herokuapp.com/download?url=https://www.youtube.com/watch?v=hVv331iLMXM";
        this.bigWaveSurfer.load(url);

        this.calcBmp = () => {
            fetch(url)
            .then( response => response.arrayBuffer())
            .then( buffer => {
                const audioContext = new AudioContext();
                return audioContext.decodeAudioData(buffer);
            }).then( audioBuffer => analyze(audioBuffer))
            .then(tempo => console.log(tempo))

        }
        //analyze()
    }

    render() {
        const { Provider } = WaveSurferContext
        return (
            <div style={{height: 250 + 'px'}}>
                {this.props.children}
                <Provider value={WaveSurferContext}>
                    <WaveSurferC type="master" height="130" url="" name={ this.props.name } />            
                </Provider>

                <div ref={ this.mainWave } style={{height:130 + 'px'}} >  </div>
                <div ref={ this.smallWave }  style={{height: 50 + 'px'}} >  </div>
            </div>
            )
        }
    
}

const mapStateToProps = (state, ownProps) => ({
    state : state.console.deck[ownProps.name],
})

const mapDispachToProps = dispach => ({
    
})

export default connect(mapStateToProps)(Deck);
