import React, { useMemo } from "react";
import { connect } from "react-redux";
import "./track-info.scss";
import track_thumbnail from "./../../../assets/img/track_thumbnail.png";

import { calcBmp } from "./../../../utils/bmp/converter.js"

const TrackInfo = props => {
    let track = useMemo(()=>({
        ...props.track
    }), [props.track]);

    console.log(track)
    return (
        <div className={"track-info " + "deck-" + props.name}>
            <div className="track-info-thumbnail">
                <img 
                alt="track thumbnails"
                src={track.details.thumbnail?.default.url || track_thumbnail}></img>
            </div>
            <div className="track-info-description">
                <span className="track-info-title">
                    {track.details.title}
                </span>
            </div>
            <div className="track-info-time">
                <span className="time-left">
                    { track.state.timeLeft || track.details.duration }
                </span>
                <span className="track-duration">
                    { track.details.duration }
                </span>
            </div>
            <div className="track-bmp">
                <span className="track-bmp-current">
                    { calcBmp(track.details.bmp, track.state.pitch) }
                </span>
                <span className="tarck-bmp-pitch">
                    { track.state.pitch + "%"}
                </span>
                <span className="track-bmp-init">
                    { track.details.bmp }
                </span>
            </div>
        </div>
    )
}

const mapStateToProps = (state, ownProps) => ({
    track : {
        details : state.console.deck[ownProps.name].track,
        state : state.console.deck[ownProps.name].state,
    }
})



export default connect(mapStateToProps)(TrackInfo);
