import React, { useEffect, useState } from "react";
import {Row, Col, Container} from "react-bootstrap"
import PlaybackButton from "./PlaybackButton/PlaybackButton";
import ProgressSlider from "./ProgressSlider/ProgressSlider";
import RecordDetails from "./RecordDetails/RecordDetails";
import "./player-controls.scss";

const PlayerControlsFooter = ({playback, seek, player, list:recordList}) => {
    const [currentRecord , setCurrentRecrod] = useState(null);
    const [currentRecordDatils, setCurrentRecrodDails] = useState(null);

    useEffect((player) => {
        if(!player){
            return;
        }
        player.addCurrentWatcher(setCurrentRecrod);
    }, [player])

    useEffect(()=>{
        console.log('current record changeged', recordList)
        if(!recordList || !recordList.length){
            return;
        }
        setCurrentRecrod(recordList.find( record => 
            {   console.log(record.id, currentRecord.id)
                return (record.id === currentRecord.id)}
         ))
    }, [currentRecord, setCurrentRecrodDails])

    return (
        <div className="record-player-controls_container">
            <Container>
                <Row>
                    <Col>
                        <div className="record-player-controls">
                            <PlaybackButton palyback={playback}/>
                            <ProgressSlider seekt={seek} player={player} />
                            <RecordDetails {...currentRecordDatils}/>
                        </div>
                    </Col>
                </Row>
            </Container>

        </div>
    )
}

export default PlayerControlsFooter;
