import {  ACTIONS } from "./../actions/actions.js";
let initState = {
    leftTrack : {
        title : null, 
        image : null,
        url : null
    },
    rightTrack : {
        title : null, 
        image : null,
        url : null
    }
}
function trackReducer(state = initState, action){
    switch(action.type){
        case ACTIONS.ADD_LEFT_TRUCK : {
            return {
                ...state,
                leftTrack : {
                    ...state.leftTrack, 
                    ...action.data
                }
            }
        }
        case ACTIONS.ADD_RIGHT_TRUCK : {
            return {
                ...state,
                rightTrack : {
                    ...state.rightTrack, 
                    ...action.data
                }
            }
        }
        default : return state;
    }
}

export default trackReducer;
