import React, { useCallback, useMemo , useState, useContext} from "react"
import PlaybackButton, { PLAYBACK_STATE } from "./PlaybackButton/PlaybackButton";
import GenereList from "./GenereList/GenereList";
import WaveSurrferPlayer from "./WaveSurrferPlayer/WaveSurferPlayer";
import Time from "./Time/Time";
import { Link } from "react-router-dom";


const Record = ({ item , seek, playback, playerBus }) => {
   // console.log(item)

    const btnCallAction = useCallback((state)=> {
        if(playback){
            playback(item.id , state);
        }
    }, [playback, item])


    return (
        <li className="record">
            <header>
                <nav>
                    <Link to={"/my/records/" + item.title} >
                        <h6 className="record-title">{item.title}</h6>
                    </Link>
                </nav>
                <aside>
                    <GenereList list={item.genere} />
                </aside>
            </header>
            <main>
                <PlaybackButton call={btnCallAction} id={item.id} playerBus={playerBus}/>
                <WaveSurrferPlayer seekHandler={seek} item={item} key={item.id} playerBus={playerBus}/>
                <Time item={item} playerBus={playerBus} />
            </main>
        </li>
    )
}

export default Record
