import React, { useEffect, useState, useCallback, useMemo } from "react";
import WaveSurfer from "wavesurfer";
import style from "./wave-surfer-player.scss";
import useWindowSize from "../../../../Hooks/useWindowSize";
import debounce from "lodash/debounce";

const WaveSurferPlayer = ({ item: { peaks, id, duration },  seek , player }) => {
    const [container, setContainerRef] = useState(null);
    const [ws, setWs] = useState(null);
    const [progress, setProgress] = useState(0);
    const [loaded, setLoaded] = useState(false);
    const [windowWidth, ] = useWindowSize()

    const containerRef = useCallback((ref) => {
        setContainerRef(ref);
    }, [setContainerRef])

    const drawPeaks = useCallback((container, peaks, ws) => {
        if(!container || !peaks || !ws ){
            return;
        }
        let width = container?.getBoundingClientRect().width;
        const start = 0;
        const end = peaks.length / 2;
        if (window.devicePixelRatio) {
            width *= window.devicePixelRatio;
        }
        ws.drawer.drawPeaks(peaks, width, start, end);
        setLoaded(true)
    }, [setLoaded])

    useEffect(() => {
        if (container && !loaded) {
            const ws = new Promise((res, rej) => {
                try {
                    const ws = WaveSurfer.create({
                        container: container,
                        barWidth: 2,
                        height: 100,
                        waveColor: style.waveColor,
                        progressColor: style.progressColor,
                        barMinHeight: 0,
                        hideScrollbar: true,
                    })
                    res(ws)
                } catch (err) {
                    rej(err)
                }
            })
            ws.then(ws => {
                setWs(ws);
                return ws;
            })

            ws.then((ws) => {
                if (peaks) {
                    drawPeaks(container, peaks, ws);
                }
                if (seek) {
                    ws.on('seek', (progress) => {
                        seek({
                            id,
                            progress,
                            duration,
                        })
                        ws.drawer.progress(progress);
                    })
                }
            })
        }
    }, [
        id, 
        seek,
        peaks,
        duration, 
        container, 
        setWs, 
        drawPeaks, 
        loaded])

    useEffect(() => {
        if (ws && progress) {
            ws.drawer.progress(progress);
        }
    }, [ws, progress])

    const getProgressData = useCallback(() => {
        return {
            duration,
            progress,
        }
    }, [duration, progress])

    useEffect(() => {
        if (!player) {
            return;
        }
        player.subscribeProgress(id, setProgress);
        player.addProgressProvider(id, getProgressData);
        return () => {
            player.unSubscribeProgress(id);
            player.removeProgressProvider(id);
        }
    }, [id, player, getProgressData, drawPeaks])


    const debouncedUpdate = useMemo(()=>{
        return debounce(drawPeaks.bind(null, container, peaks, ws), 500)
    }, [ws, peaks , container])

    useEffect(()=>{
        if(loaded){
            debouncedUpdate();
        }
    }, [windowWidth, loaded])

    return (
        <div key={"ws" + id} className="ws-player-container" ref={containerRef} />
    )
}

export default WaveSurferPlayer
