export function toDbValue(value) {
    let gain = 1 + value / 100;
    let dB = Math.floor(20 * Math.log10(gain));
    return dB;  
}

export function toDbValueText (value) {
    let dB = toDbValue(value)
    dB = (dB === -Infinity) ? "-\u221E"  : dB + "dB";
    return  dB ;
}
