import config from "./../../../../components/Deck/Player/configuration";
import WaveSurfer from "wavesurfer";
import {
  setChannelReady,
  setLoadingProgress,
  setTimeLeft
} from "./../../../../actions";
import store from "./../../../../store";

export default class ChannelBuilder {
  constructor() {
    this.dispatch = store.dispatch;
  }

  create(channelName, masterContainer, slaveContainer) {
    let channel = {channelName, masterContainer, slaveContainer};

    channel.masterConfig = config.master(masterContainer, channelName);
    channel.slaveConfig = config.slave(slaveContainer, channelName);

    channel.master = WaveSurfer.create(channel.masterConfig);
    channel.master.__proto__.loadWithEvent = function(...args) {
      this.fireEvent("load");
      this.load(...args);
    };
    channel.slave = WaveSurfer.create(channel.slaveConfig);

    this.attachEvents(channel);

    return channel.master;
  }

  attachEvents(channel) {
    channel.master.on("load", () => {
      this.clearState(channel);
    });

    channel.master.on("loading", progress => {
      this.dispatch(setLoadingProgress(channelName, progress));
    });

    channel.master.on("ready", () => {
      channel.currentDuration = channel.master.getDuration();
      let resolution = 430;
      channel.slave.load(
        null,
        channel.master.backend.getPeaks(resolution, 0, resolution)
      );
      this.dispatch(setLoadingProgress(channelName, 100));

      this.dispatch(setChannelReady(true, channelName));
      this.startSync(channel);
    });

    channel.slave.on("seek", progress => {
      const wasPlaying = channel.master.isPlaying();

      channel.master.backend.seekTo(progress * channel.currentDuration);
      channel.master.drawer.progress(progress);
      channel.master.fireEvent("seek", progress);

      channel.slave.drawer.progress(progress);

      const timeLeft =
        channel.master.getDuration() - channel.master.getDuration() * progress;
      this.dispatch(setTimeLeft(channelName, timeLeft));
      if (wasPlaying) {
        channel.master.play();
      }
    });
  }

  clearState(channel) {
    channel.slave.load(null, []);
    if (channel.barsElements) {
      channel.barsElements.forEach(htmlElement => htmlElement.remove());
    }
  }

  startSync = channel => {
    channel.syncHandle = setInterval(() => {
      channel.slave.drawer.progress(
        channel.master.getCurrentTime() / this.currentDuration
      );
    }, 100);
  };
}
