export default class Mastering {
    constructor(mixer) {
        this.mixer = mixer;


        this.defaultCompressor = {
            threshold: 0,
            ratio: 1,
            attack: .003,
            release: 0.25,
        }
    }

    getMain() {
        console.log(this.mixer.audioNodes.channels['main'])
        return this.mixer.audioNodes.channels['main'];
    }

    setMasterParam(status, value) {
        console.log(status, value)
    }

    configCompressor() {
        const compressor = this.getMain().compressor;
        console.log(compressor)
       /* Object.entries(this.defaultCompressor).forEach(([variable, value]) => {
            this.setAudioParam(compressor, variable, value)
        });*/
    }

    setAudioParam(audioNode, variable, value) {
        audioNode[variable].setTargetAtTime(value, this.mixer.mainAudioContext, 0.01)
    }
}
