import React, { useState, useRef, useEffect } from "react";
import "./progress-slider.scss"

//horizontal
const ProgressSlider = ({player}) => {
    const [progress, setProgress] = useState(0)
    const [position,  setPosition] = useState();
    const [currentTime, setCurrentTime] = useState();
    
    const sliderArea = useRef(null);
    const sliderThumb = useRef(null);
    const sliderProgress = useRef(null);
    const sliderBufferd = useRef(null);

    useEffect(()=>{
        console.log(progress)
    }, [progress])

    useEffect(()=>{
        if(!player){
            return;
        }
        player.setProgressHandler('#', setProgress);
    }, [player])

    return (
        <div className="seek-slider">
            <div className="time time-current">
                <span></span>
            </div>
            <div className="slider-active-area" ref={sliderArea}>
                <div className="slider-thumb" ref={sliderThumb}/>
                <div className="slider-progress" ref={sliderProgress}/>
                <div className="slider-buffered" ref={sliderBufferd}/>
            </div>
            <div className="time time-duration">
                <span></span>
            </div>
        </div>
    
    )
}

export default ProgressSlider;
