import React from "react";
import "./comment.scss"

const Comment = ({}) => {


    return (
        <Form onSubmit={submitComment}
            onFocus={setShowButton.bind(null, true)}
            onBlur={onBlur}>
            <Form.Group>
                <Form.Label>
                    Post your comment:
                </Form.Label>
                <Form.Control 
                    className="record-comments-control"
                    type="textarea" 
                    as="textarea"
                    name="comment-conntent"
                    value={comment}

                    onChange={updateComment}/>
            </Form.Group>
            {showButton && <Button type="submit">Post</Button>}
        </Form>
    )
}
