import React, {useEffect, useMemo, useState} from "react";

const getMarkups = () => require.context('./../markups', true, /\.md$/).keys();

const Menu = () => {
    const [markups, setMarkups] = useState();

    useEffect(()=>{
        setMarkups(getMarkups())
    }, [setMarkups])

    const items = useMemo(() => {
        let items = markups.map( markup => markup.substr(2, markup.length))
                           .map( markup => markup.split("/"));
        let structure = new Map(items);
        structure = structure.values().map( item => {
            let first = item.shift();
            if(structure.has(first))
            return item;
        })
        return null
    }, [markups])

    console.log(markups);

    return (
        <aside className="introduction__menu">
           <nav>
                I'm menu
           </nav>
        </aside>
    )
}

export default Menu;
