import React, { useState , useEffect, useRef} from "react";
import "./pitch-control.scss";

const PitchControl= props =>{

    const [sliderRange, setSliderRange] = useState(null);
    const [sliderPosition, setSliderPosition ] = useState(null);
    const sliderRangeElement = useRef();
    const sliderThumbElement = useRef();
    const sliderAreaElement = useRef();


    const clickHandle = event =>{
        if(event.target === sliderAreaElement.current || event.target === sliderRangeElement.current){ // area clicked
        
            let sliderTop = sliderAreaElement.current.getBoundingClientRect().top;
            console.log(event.clientY -  sliderTop);
        } else if(event.target === sliderThumbElement.current){ // thumb clicked
            console.log('clicked thumb');
        }
    }

    useEffect(() => {
        setSliderPosition(window.getComputedStyle(sliderThumbElement.current).top);
        setSliderRange(window.getComputedStyle(sliderRangeElement.current).height);

        sliderAreaElement.current.addEventListener('click', clickHandle);
    }, [])

    return (
        <div className={"pitch-control " + "pitch-" + props.name}>
            <div ref={sliderAreaElement} className="pitch-area">
                <div ref={sliderRangeElement} className="slider-range"/>
                <div ref={sliderThumbElement}className="slider-thumb"/>
            </div>
        </div>
    )
}

export default PitchControl;
