import React, { useCallback, useMemo } from 'react';
import icons from "./emoji.json";
const iconsOnPanel = 30;

const EmotiContainer = ({ onSelect, node }) => {


    const iconsPanels = useMemo(() => {
        let pannels = [];
        for (let i = 0; i < icons.length; i + iconsOnPanel) {
            let pannel = [];
            for (let j = i; (j < iconsOnPanel) && (i + j < icons.length); j++) {
                let icon = icons[i]
                pannel.push(
                    <button onSelect={onSelect.bind(null, icon.emoji)}>{icon.emoji}</button>
                )
            }
            pannels.push(
                <section className="emoticons-panel">
                    {pannel}
                </section>
            )
        }
        return panels;
    })

    console.log("render emoti")
    return (
        <div className="emoticons-container">
            {iconsPanels}
        </div>
    )
}

export default EmotiContainer;
