import React, { useCallback, useState, Fragment } from "react";
import { connect } from "react-redux";
import "./explorer-tree.scss";
import { toggleDir, setSelection} from "./../../../../../../actions"
import { isEmpty, sortObj } from "./../../../../../../utils/objects/helpers";
import UUID from "uuidjs";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import ClassName from "classnames";
import { faFolder,  faFile, faAngleRight, faAngleDown } from "@fortawesome/free-solid-svg-icons";

const ExplorerTree = props => {

    const toogleDir = useCallback((path) => {
        console.log("doubel clicked")
        props.toogleDir(path);
    }, [props.toogleDir])

    const setSelection = useCallback((path)=>{
        props.setSelection(path);
    }, [props.setSelection])

    const [lastClick, setLastClick] = useState(null);
    const [timerHandler, setTimerHandler] = useState(null);
    const liClickHandler = useCallback((path)=>{
        if(timerHandler === null){
            setTimerHandler(setTimeout(()=>{
                setSelection.bind(null, path)
            }), 500)
        }
    }, [setLastClick, setTimerHandler, lastClick, timerHandler])

    const getDirElement = useCallback((name, path, open, empty) => {
        const liClassList = ClassName(
            "list-item item-dir",
            {"item-dir--empty" : empty},
            {"item--selected": [...path, name].join("") === props.currentSelection.join("")}
        )
        return (
            <li
               // onDoubleClick={e=>console.log("a")}
                onClick={liClickHandler([...path, name])}
                className={liClassList}
                data-path={path.join("/")}
                key={UUID.genV1()}>
                {!empty &&
                    <button
                        className={"btn-dir " + ((open) ? "btn-dir--expand" : "btn-dir--colapse")}>
                        <FontAwesomeIcon 
                            className="icon" 
                            icon={(open) ? faAngleDown : faAngleRight } 
                            onClick={toogleDir.bind(null, [...path, name])}/>
                    </button>}
                    <FontAwesomeIcon className="icon icon-dir" icon={faFolder} />
                    {name}
            </li>
        )
    })

    const getFileElement = useCallback((name, path) => {
        const liClassList = ClassName(
            "list-item item-file",
            {"item--selected": [...path, name].join("") === props.currentSelection.join("")}
        )
        return (
            <li
                className={liClassList}
                onClick={setSelection.bind(null, [...path, name])}
                data-path={path.join("/")}
                key={UUID.genV1()}>
                <FontAwesomeIcon className="icon icon-file" icon={faFile} />
                {name}
            </li>
        );
    })

    const renderDirElements = useCallback((name, content, path) => {
        content = sortObj(content);
        if (content instanceof Array) {
            return getFileElement(name, path)
        } else {
            if (content._open === true) {
                return (
                    <Fragment key={UUID.genV1()}>
                        {getDirElement(name, path, true)}
                        {renderDir(content, [...path, name])}
                    </Fragment>
                )
            } else {
                if (isEmpty(content)) {
                    return getDirElement(name, path, false, true)
                }
                return getDirElement(name, path, false)
            }
        }
    })


    const renderDir = useCallback((dir, path) => {
        return (
            <ul className="list-dir dir-content" key={UUID.generate()}>
                {
                    Object.entries(dir).map(([name, content]) => {
                        if (name === "_open") {
                            return null;
                        }
                        return renderDirElements(name, content, path);
                    })
                }
            </ul>
        )
    })

    return (
        <div className="explorer-tree">
            {renderDir(props.root, ['root'])}
        </div>
    )
}

const mapStateToProps = state => ({
    root: state.playList.root,
    currentSelection : state.playList.currentSelection
})

const mapDispatchToProps = dispatch => ({
    toogleDir: path => dispatch(toggleDir(path)),
    setSelection : path => dispatch(setSelection(path))
})

export default connect(mapStateToProps, mapDispatchToProps)(ExplorerTree);
