import React, { useCallback, useMemo } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faArrowCircleLeft ,faArrowCircleRight} from "@fortawesome/free-solid-svg-icons";
import UUID from 'uuidjs'
import icons from "./emoji.json";
const iconsOnPanel = 30;

const buttonStyle = {
    background: 'transparent',
    border: 'none',
    boxShadow: 'none',
    outline: 'none',
    width: '15%',
    height: '20%'
}

const sectionStyle = {
    width: '100%',
    height: '100%',
    display: 'inline-block',
    verticalAlign : 'top',
    whiteSpace: 'normal'
}

const sliderContainer = {
    overflow : 'hidden',
    width: '200px',
    height: '200px',
    whiteSpace: 'nowrap',

    position: 'sticki',
    bottom: '50px',


}

const containerStyle = {
    position: 'absolute',
    transform: 'translate(-100px ,-120%)',
    width: '250px',
    height: '250px',
    background: 'white',
}

const EmotiContainer = ({ onSelect, node }) => {


    const iconsPanels = useMemo(() => {
        let panels = [];
        for (let i = 0; i < icons.length; i += iconsOnPanel) {
            let panel = [];
            for (let j = i; (j < iconsOnPanel + i) && (i + j < icons.length); j++) {
                let icon = icons[j]
                panel.push(
                    <button key={'icon-'+ (i+j)} style={buttonStyle} onSelect={onSelect.bind(null, icon.emoji)}>{icon.emoji}</button>
                )
            }
            panels.push(
                <section style={sectionStyle} className="emoticons-panel" key={'pannel-'+i}>
                    {panel}
                </section>
            )
        }


        return panels;
    }, [])

    console.log("render emoti")
    return (
        <div className="emoticons-container" style={containerStyle}>
            <button className="btn-prev" style={buttonStyle}>
                    <FontAwesomeIcon icon={faArrowCircleLeft}/>
            </button>
            <div className="emoticons-popup" style={sliderContainer}>
                 {iconsPanels}
            </div>
            <button className="btn-prev" style={buttonStyle}>
                    <FontAwesomeIcon icon={faArrowCircleRight}/>
            </button>
        </div>
    )
}

export default EmotiContainer;
