import { useAuth0 } from "../../auth0/react-auth0-spa";


export default getAuthHeader = async () => {
    const { getTokenSilently } = useAuth0();
    const token = await getTokenSilently();

    return ({
            Authorization: `Bearer ${token}`
    })
}
