import React from "react";
import Slider from "./Slider/Slider";
//import "./pitch-control.scss";

class PitchControl extends React.Component{
    constructor() {
        super();

    }




    render() {
        return (
            <div className={"pitch pitch-" + this.props.name} style={{display:'flex'}}>
                <Slider className="pitch" from={-8} to={8} onChange={this.props.onChange}/>
            </div>
        )
    }
}



export default PitchControl;
