export default function useTransitionEnd(element = window){
    const subscribe = (handle) => {
        window.addEventListener("transitionend", handle);
        window.addEventListener("transitioncancel", handle)
        window.addEventListener("mozTransitionEnd",handle);
        window.addEventListener("webkitTransitionEnd",handle);
        window.addEventListener("oTransitionEnd",handle);
    }

    const unSubscribe = (handle) => {
        window.removeEventListener("transitionend",handle);
        window.removeEventListener("transitioncancel",handle)
        window.removeEventListener("mozTransitionEnd",handle);
        window.removeEventListener("webkitTransitionEnd",handle);
        window.removeEventListener("oTransitionEnd",handle);
    }

    return [subscribe, unSubscribe];
}
