//const map = require('unist-util-map');
const filter = require('unist-util-filter')
const vFile = require('vfile');


module.exports = attacher

const newFileFrom = (vfile, contents) => {
    return vFile({...vfile, contents: contents})
}

function attacher() {
   // console.log("the this", this.Parser);
    const pattern = /{\.(\w+)}/;
    const processor = this;
    function transformer(tree, file) {
        let inlineMethods =processor.Parser.prototype.inlineMethods;
        let blockMethods = processor.Parser.prototype.blockMethods;
        console.log(tree)
        const modify = node => {
            if(!node.children) return node;
            let className = null;
            const children = [];
            node.children.forEach(node=> {
                if(node.type === 'cssClass'){
                    className = node.value;
                    return;
                }
                if(className){
                    node.className = className;
                    node.data = {
                        hProperties: {className: className},
                    }
                    className = null;
                }
                if(node.children && node.children.length){
                    node = modify(node);
                }
                children.push(node);
            })

            node.children = children;
            return node;
        }

        tree = modify(tree)

        let next = filter(tree, node => node.type !== "cssClass")

        return next;
    }

    return transformer
}
